import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Register.css";

function Register() {
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [showOtpInput, setShowOtpInput] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [countdown, setCountdown] = useState(180); // 180 วินาที (3 นาที)
  const [timerActive, setTimerActive] = useState(false); // ใช้ตรวจสอบว่าตัวจับเวลาทำงานอยู่หรือไม่
  const navigate = useNavigate();

  const startCountdown = () => {
    setTimerActive(true);
    let timeLeft = 180; // 3 นาที
    setCountdown(timeLeft);

    const timer = setInterval(() => {
      timeLeft -= 1;
      setCountdown(timeLeft);

      if (timeLeft <= 0) {
        clearInterval(timer);
        setTimerActive(false);
      }
    }, 1000);
  };

  const handleRequestOtp = async () => {
    try {
      const response = await fetch("http://localhost:5000/send-otp", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ phone }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrorMessage(errorData.message || "เกิดข้อผิดพลาดในการส่ง OTP");
        return;
      }

      const data = await response.json();
      console.log("Response:", data);
      setErrorMessage(""); // ล้างข้อความข้อผิดพลาด
      setShowOtpInput(true); // แสดงช่องกรอก OTP
      startCountdown(); // เริ่มตัวจับเวลา
    } catch (error) {
      console.error("Error sending OTP:", error);
      setErrorMessage("ไม่สามารถเชื่อมต่อกับเซิร์ฟเวอร์ได้");
    }
  };

  const handleVerifyOtp = async () => {
    try {
      const response = await fetch("http://localhost:5000/verify-otp", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ phone, otp }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        setErrorMessage(errorData.message || "OTP ไม่ถูกต้อง");
        return;
      }

      const data = await response.json();
      console.log("OTP Verified:", data);
      setErrorMessage(""); // ล้างข้อความข้อผิดพลาด
      navigate("/profile-setup"); // เปลี่ยนไปยังหน้า ProfileSetup
    } catch (error) {
      console.error("Error verifying OTP:", error);
      setErrorMessage("ไม่สามารถตรวจสอบ OTP ได้");
    }
  };

  const handleOtpChange = (index, value) => {
    const otpArray = otp.split("");
    otpArray[index] = value;
    setOtp(otpArray.join(""));
  };

  return (
    <div className="register-container">
      <h1>ลงทะเบียน</h1>
      <div className="register-form">
        <label htmlFor="phone">เบอร์โทรศัพท์</label>
        <div className="phone-input">
          <span>🇹🇭 +66</span>
          <input
            type="text"
            id="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            placeholder="กรอกเบอร์โทรศัพท์"
          />
        </div>
        {errorMessage && <p className="error-message">{errorMessage}</p>}
        <button onClick={handleRequestOtp}>ขอรหัส OTP</button>
        {showOtpInput && (
          <div className="otp-form">
            <label htmlFor="otp">รหัส OTP</label>
            <input
              type="text"
              id="otp"
              value={otp}
              onChange={(e) => setOtp(e.target.value)}
              placeholder="กรอกรหัส OTP"
            />
            <button onClick={handleVerifyOtp}>ยืนยัน OTP</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default Register;
