import { useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";

export default function UploadPage() {
  const [message, setMessage] = useState("");
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const history = useHistory();

  useEffect(() => {
    const orderDetails = JSON.parse(localStorage.getItem("orderDetails"));
    if (!orderDetails) {
      alert("ไม่มีข้อมูลคำสั่งซื้อ");
      history.push("/select");
    }
  }, [history]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file && !file.type.startsWith("image/")) {
      alert("กรุณาเลือกรูปภาพเท่านั้น");
      return;
    }
    setImage(file);
    setPreview(URL.createObjectURL(file));
  };

  const handleUpload = async () => {
    if (!image && !message) {
      alert("กรุณาเพิ่มข้อความหรือเลือกรูปภาพก่อนอัปโหลด");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    formData.append("message", message);

    try {
      const res = await axios.post("http://localhost:3001/upload-content", formData);
      alert("อัปโหลดสำเร็จ!");

      // เปลี่ยนเส้นทางไปยังหน้าชำระเงิน
      history.push("/payment");
    } catch (error) {
      console.error("Error uploading content:", error.response || error.message);
      alert("เกิดข้อผิดพลาดในการอัปโหลด");
    }
  };

  return (
    <div className="flex flex-col items-center p-6 space-y-4">
      <h1 className="text-2xl font-bold">Upload Content</h1>
      <textarea
        className="border p-2 w-96 h-20"
        placeholder="Enter your message..."
        value={message}
        onChange={(e) => setMessage(e.target.value)}
      />
      <input type="file" accept="image/*" onChange={handleImageChange} />
      {preview && <img src={preview} alt="Preview" className="h-40 mt-2" />}
      <button className="bg-blue-500 text-white p-2 rounded" onClick={handleUpload}>
        Upload
      </button>
    </div>
  );
}
