import { useState, useEffect } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import "./UploadPage.css";

export default function UploadPage() {
  const [message, setMessage] = useState("");
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const history = useHistory();

  useEffect(() => {
    const orderDetails = JSON.parse(localStorage.getItem("orderDetails"));
    if (!orderDetails) {
      alert("ไม่มีข้อมูลคำสั่งซื้อ");
      history.push("/select");
    }
  }, [history]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file && !file.type.startsWith("image/")) {
      alert("กรุณาเลือกรูปภาพเท่านั้น");
      return;
    }
    setImage(file);
    setPreview(URL.createObjectURL(file));
  };

  const handleUpload = async () => {
    if (!image && !message) {
      alert("กรุณาเพิ่มข้อความหรือเลือกรูปภาพก่อนอัปโหลด");
      return;
    }

    const formData = new FormData();
    formData.append("image", image);
    formData.append("message", message);

    try {
      const res = await axios.post("http://localhost:3001/upload-content", formData);
      alert("อัปโหลดสำเร็จ!");

      // เปลี่ยนเส้นทางไปยังหน้าชำระเงิน
      history.push("/payment");
    } catch (error) {
      console.error("Error uploading content:", error.response || error.message);
      alert("เกิดข้อผิดพลาดในการอัปโหลด");
    }
  };

  return (
    <div className="flex flex-col items-center justify-center p-6 space-y-6 bg-gradient-to-b from-blue-100 to-blue-300 min-h-screen">
      <div className="bg-white shadow-lg rounded-lg p-8 w-full max-w-lg">
        <h1 className="text-3xl font-bold text-blue-600 text-center mb-4">Upload Content</h1>
        <p className="text-gray-600 text-center mb-6">
          กรุณาเพิ่มข้อความหรือเลือกรูปภาพเพื่ออัปโหลด
        </p>
        <textarea
          className="border border-gray-300 rounded-lg p-3 w-full h-24 focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
          placeholder="Enter your message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
        />
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          className="block w-full text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100 mb-4"
        />
        {preview && (
          <div className="mt-4 flex justify-center">
            <img
              src={preview}
              alt="Preview"
              className="h-40 w-auto rounded-lg shadow-md"
            />
          </div>
        )}
        <button
          className="bg-blue-500 hover:bg-blue-600 text-white font-bold py-2 px-6 rounded-lg shadow-md w-full mt-6"
          onClick={handleUpload}
        >
          Upload
        </button>
      </div>
    </div>
  );
}
