import React, { useState } from "react";
import { useLocation, useHistory } from "react-router-dom";
import "./Upload.css";

function Upload() {
  const location = useLocation();
  const history = useHistory();
  const queryParams = new URLSearchParams(location.search);
  const type = queryParams.get("type");
  const time = parseInt(queryParams.get("time")); // เวลาที่เลือกในนาที
  const price = queryParams.get("price");

  const [text, setText] = useState("");
  const [image, setImage] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [showPreviewModal, setShowPreviewModal] = useState(false);
  const [alertMessage, setAlertMessage] = useState("");
  const [isProcessing, setIsProcessing] = useState(false);

  const handleTextChange = (e) => setText(e.target.value);
  const handleImageChange = (e) => setImage(e.target.files[0]);

  const handleUpload = async () => {
    if (type === "image" && !image) {
      setAlertMessage("โปรดใส่ไฟล์ก่อน");
      return;
    }

    if (type === "text" && !text) {
      setAlertMessage("โปรดกรอกข้อความก่อน");
      return;
    }

    setIsProcessing(true); // เริ่มสถานะการประมวลผล

    try {
      const formData = new FormData();
      if (type === "image") {
        formData.append("image", image);
      }
      formData.append("text", text);

      // ส่งข้อมูลไปยังเซิร์ฟเวอร์
      await fetch("http://localhost:5000/upload", {
        method: "POST",
        body: formData,
      });

      alert("อัปโหลดสำเร็จ!");

      // เปลี่ยนเส้นทางไปยังหน้าชำระเงิน พร้อมส่งพารามิเตอร์
      history.push(`/payment?type=${type}&time=${time}&price=${price}`);
    } catch (error) {
      console.error("Error uploading content:", error);
      alert("เกิดข้อผิดพลาดในการอัปโหลด");
    } finally {
      setIsProcessing(false); // หยุดสถานะการประมวลผล
    }
  };

  const handleAccept = () => {
    setShowPreviewModal(false);
    handleUpload();
  };

  const handleEdit = () => {
    setShowPreviewModal(false);
  };

  const handleShowModal = () => setShowModal(true);
  const handleCloseModal = () => setShowModal(false);

  return (
    <div className="upload-container">
      <header className="upload-header">
        <h1>อัปโหลด {type === "image" ? "รูปภาพ" : "ข้อความ"}</h1>
      </header>
      <main className="upload-main">
        <p>เวลา: {time} นาที</p>
        <p>ราคา: {price} บาท</p>
        {type === "image" && (
          <>
            <input type="file" accept="image/*" onChange={handleImageChange} />
            <br />
            {image && (
              <div className="image-preview">
                <img src={URL.createObjectURL(image)} alt="Preview" />
                <div className="image-text">{text}</div>
              </div>
            )}
          </>
        )}
        <div className="input-container">
          <textarea
            placeholder="พิมพ์ข้อความที่ต้องการส่งขึ้นจอ..."
            value={text}
            onChange={handleTextChange}
            rows={4}
            cols={50}
          />
        </div>
        <br />
        <button className="restrictions-button" onClick={handleShowModal}>
          ข้อกำหนด
        </button>
        <button
          className="continue-button"
          onClick={() => setShowPreviewModal(true)}
          disabled={isProcessing}
        >
          {isProcessing ? "กำลังอัปโหลด..." : "ดำเนินการต่อ"}
        </button>

        {alertMessage && <p className="alert-message">{alertMessage}</p>}

        {showModal && (
          <div className="modal">
            <div className="modal-content">
              <span className="close-button" onClick={handleCloseModal}>
                &times;
              </span>
              <h3>ข้อกำหนด {type === "image" ? "รูปภาพ" : "ข้อความ"}</h3>
              <ul>
                {type === "image" ? (
                  <>
                    !!! ห้ามใช้รูปภาพดังต่อไปนี้
                    <li>รูปภาพสำหรับการโฆษณาที่ละเมิดกฎหมาย...</li>
                  </>
                ) : (
                  <>
                    !!! ห้ามใช้ข้อความดังต่อไปนี้
                    <li>ข้อความสำหรับการโฆษณาที่ละเมิดกฎหมาย...</li>
                  </>
                )}
              </ul>
            </div>
          </div>
        )}

        {showPreviewModal && (
          <div className="modal">
            <div className="modal-content">
              <span className="close-button" onClick={handleEdit}>
                &times;
              </span>
              <h3>ยืนยันการแชร์วาร์ป</h3>
              {type === "image" && image && (
                <div className="image-preview">
                  <img src={URL.createObjectURL(image)} alt="Preview" />
                  <div className="image-text">{text}</div>
                </div>
              )}
              {type === "text" && <p>{text}</p>}
              <button className="accept-button" onClick={handleAccept}>
                ยอมรับ
              </button>
              <button className="edit-button" onClick={handleEdit}>
                แก้ไข
              </button>
            </div>
          </div>
        )}
      </main>
      <footer className="home-footer">
        <p>© 2025 Digital Signage Content Management System</p>
      </footer>
    </div>
  );
}

export default Upload;
