import React, { useState } from "react";
import { useHistory, useLocation } from "react-router-dom";
import axios from "axios"; // ใช้ axios สำหรับเรียก API
import "./Payment.css";
import promptpayLogo from "./data-icon/promptpay-logo.png";
import truemoneyLogo from "./data-icon/truemoney-logo.png";
import paymentLogo from "./data-icon/payment-logo.jpg"; // นำเข้ารูป payment-logo.jpg

function Payment() {
  const history = useHistory();
  const location = useLocation();
  const queryParams = new URLSearchParams(location.search);
  const type = queryParams.get("type");
  const time = queryParams.get("time");
  const price = queryParams.get("price");

  const [paymentMethod, setPaymentMethod] = useState(null);
  const [showPopup, setShowPopup] = useState(false);
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [otpSent, setOtpSent] = useState(false);
  const [otpVerified, setOtpVerified] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");

  const handleConfirmPayment = () => {
    alert("การชำระเงินสำเร็จ!");
    localStorage.setItem('order', JSON.stringify({ type, time, price }));
    history.push("/");
  };

  const handlePaymentSelection = (method) => {
    setPaymentMethod(method);
    setShowPopup(true);
  };

  const closePopup = () => {
    setShowPopup(false);
  };

  const sendOtp = async () => {
    try {
      await axios.post("/send-otp", { phone }); // ใช้ Proxy เพื่อเชื่อมต่อกับ Backend
      setOtpSent(true);
      setErrorMessage("");
      alert("OTP ถูกส่งไปยังหมายเลขโทรศัพท์ของคุณแล้ว");
    } catch (error) {
      console.error("Error sending OTP:", error.response || error.message); // แสดงข้อผิดพลาดในคอนโซล
      setErrorMessage("ไม่สามารถส่ง OTP ได้");
    }
  };

  const verifyOtp = async () => {
    try {
      const response = await axios.post("http://localhost:3001/verify-otp", { phone, otp });
      if (response.data.success) {
        setOtpVerified(true);
        setErrorMessage("");
        alert("OTP ถูกต้อง");
      }
    } catch (error) {
      console.error("Error verifying OTP:", error); // แสดงข้อผิดพลาดในคอนโซล
      setErrorMessage("OTP ไม่ถูกต้อง");
    }
  };

  return (
    <div className="payment-container">
      <header className="payment-header">
        <h1>สรุปรายการคำสั่งซื้อ</h1>
      </header>
      <main className="payment-main">
        <div className="order-summary">
          <h2>Digital Signage CMS</h2>
          <p>ส่ง{type === "image" ? "รูป" : "ข้อความ"}ขึ้นจอ ({time})</p>
          <p>{price} บาท</p>
        </div>

        <h2>วิธีการชำระเงิน</h2>
        <div className="payment-methods">
          <div
            className={`payment-method ${
              paymentMethod === "promptpay" ? "selected" : ""
            }`}
            onClick={() => setPaymentMethod("promptpay")}
          >
            <img src={promptpayLogo} alt="Prompt Pay" />
            <p>Prompt Pay</p>
          </div>
          <div
            className={`payment-method ${
              paymentMethod === "truemoney" ? "selected" : ""
            }`}
            onClick={() => setPaymentMethod("truemoney")}
          >
            <img src={truemoneyLogo} alt="True Money" />
            <p>True Money</p>
          </div>
        </div>

        <button
          className="confirm-button"
          onClick={() => handlePaymentSelection(paymentMethod)}
          disabled={!paymentMethod}
        >
          ยืนยันการเลือก
        </button>

        {showPopup && paymentMethod === "promptpay" && (
          <div className="popup">
            <div className="popup-content">
              <button className="close-button" onClick={closePopup}>
                X
              </button>
              <h2>ชำระเงินผ่าน Prompt Pay</h2>
              <img src={paymentLogo} alt="Payment Logo" className="payment-logo" />
              <p>โปรดใช้พร้อมเพย์เพื่อชำระเงิน:</p>
              <ol>
                <li>แคปหน้าจอดังกล่าว</li>
                <li>เปิด Mobile Banking App บนมือถือของคุณ</li>
                <li>เลือกไปที่ 'สแกน' หรือ 'คิวอาร์โค้ด' กด 'รูปภาพ'</li>
                <li>เลือกรูปภาพที่คุณแคปไว้และทำการชำระเงิน</li>
                <li>
                  เมื่อชำระเงินเสร็จ กลับมายังหน้าชำระเงิน และรอสักครู่ ระบบจะนำท่านไปยังหน้าชำระเงินสำเร็จ
                </li>
              </ol>
              <button className="confirm-button" onClick={handleConfirmPayment}>
                ยืนยันการชำระเงิน
              </button>
            </div>
          </div>
        )}

        {showPopup && paymentMethod === "truemoney" && (
          <div className="popup">
            <div className="popup-content">
              <button className="close-button" onClick={closePopup}>
                X
              </button>
              <h2>ชำระเงินผ่าน True Money</h2>
              <p>โปรดใส่เบอร์โทรของท่านเพื่อยืนยันการชำระเงิน:</p>
              <input
                type="text"
                placeholder="เบอร์โทร"
                value={phone}
                onChange={(e) => setPhone(e.target.value)}
              />
              <button className="otp-button" onClick={sendOtp} disabled={otpSent}>
                รับรหัส OTP
              </button>
              {otpSent && (
                <>
                  <input
                    type="text"
                    placeholder="รหัส OTP"
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                  />
                  <button className="confirm-button" onClick={verifyOtp}>
                    ยืนยัน OTP
                  </button>
                </>
              )}
              {errorMessage && <p className="error-message">{errorMessage}</p>}
              {otpVerified && (
                <button className="confirm-button" onClick={handleConfirmPayment}>
                  ยืนยันการชำระเงิน
                </button>
              )}
            </div>
          </div>
        )}
      </main>
      <footer className="payment-footer">
        <p>© 2025 Digital Signage Content Management System</p>
      </footer>
    </div>
  );
}

export default Payment;
