import { useEffect, useState } from "react";
import axios from "axios";

export default function DigitalScreen() {
  const [content, setContent] = useState([]);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const res = await axios.get("http://localhost:3001/get-content");
        setContent(res.data);
      } catch (error) {
        console.error("Error fetching content:", error);
        alert("ไม่สามารถโหลดข้อมูลได้ กรุณาลองใหม่อีกครั้ง");
      }
    };
    fetchContent();
  }, []);

  return (
    <div className="flex flex-col items-center p-6">
      <h1 className="text-2xl font-bold">Digital Screen</h1>
      {content && content.length > 0 ? (
        content.map((item, index) => (
          <div key={index} className="border p-4 my-2 w-96">
            <p>{item.message}</p>
            {item.image && <img src={item.image} alt={`Uploaded content ${index}`} className="mt-2 h-40" />}
          </div>
        ))
      ) : (
        <p>ไม่มีข้อมูลที่จะแสดง</p>
      )}
    </div>
  );
}
